import React from "react";
const state = useState()

let count = 1;
const IncNum = () => {
    count++;
    console.log('clicked');
}

const Hook = () => {
    return (
        <>
            <h1>{count}</h1>
            <button onClick={IncNum}> Click</button>
        </>
    )
}
export default Hook;
